root.module:

   insert into Articles(CategoryId,Title,Editor,Media,Description,Contents,IsTop,CreatedTime,LastModifiedTime) 
   values(@CategoryId,@Title,@Editor,@Media,@Description,@Contents,@IsTop,@CreatedTime,@LastModifiedTime);
   select @@IDENTITY;


   update Articles
   set CategoryId=@CategoryId,Title=@Title,Editor=@Editor,Media=@Media,Description=@Description,Contents=@Contents,IsTop=@IsTop,LastModifiedTime=@LastModifiedTime
   where ArticleId = @Id;


   delete from Articles where ArticleId = @Id;


   select *  from Articles  where ArticleId = @Id;


   select *  from Articles;


   select * from Articles
   order by ArticleId desc


  select * from Articles
  where (Title like @Keyword)
  and (if(@CategoryId=0,1=1,CategoryId=@CategoryId))
  order by ArticleId desc
-->
